import React,{Component} from "react";

import '../assets/css/Swiper.css'
import $ from 'jquery';
import Swipe from '../library/swipe';
import '../assets/css/style.css'
import banner0 from '../assets/img/banner0.png'
import banner1 from '../assets/img/banner1.png'

class Swiper extends Component {
  state={
    list:[]
  }

  render() {
    const {list}=this.state;
    return (
      <div className="swiper-container">
            <div className="swiper-wrapper">
            {
              list.map(item=>(
                <div className="swiper-slide"><img src={list}/></div>
              ))
            }
						</div>
					</div> 
    );
  }
  componentDidMount(){
        var swiper = new Swiper('.swiper-container', {
          slidesPerView: 1,
          spaceBetween:0,
          loop: true,
          autoplay: 2500
            //autoplayDisableOnInteraction: false
      });
      $(function(){
          $(".swiper-container").height("140px");
        $(".tabs1>li").click(function(){
          var index=$(this).index();
          $(".tabs1>li").removeClass().eq(index).addClass("on");
          $(".content>li").hide().eq(index).show()
        })
	})
  }
}

export default Swiper;
